import React, { useEffect } from 'react';
import { View, Text, Button } from 'react-native';
import { Notifications } from 'expo';
import * as Device from 'expo-device';
import Constants from 'expo-constants';

export default function Cuidado() {
  useEffect(() => {
    // Solicitar permissões de notificação quando o componente for montado
    requestNotificationPermissions();
  }, []);

  const requestNotificationPermissions = async () => {
    // Solicitar permissões de notificação ao usuário
    const { status } = await Notifications.requestPermissionsAsync();
    if (status !== 'granted') {
      alert('Permissão para notificações não concedida!');
    }
  };

  const sendNotification = async () => {
    try {
      // Obter informações sobre o dispositivo
      const deviceName = Device.deviceName;
      const deviceId = Constants.installationId;

      // Enviar uma notificação com informações sobre o dispositivo
      await Notifications.scheduleNotificationAsync({
        content: {
          title: 'Informações do Dispositivo',
          body: `Nome do Dispositivo: ${deviceName}\nID do Dispositivo: ${deviceId}`,
        },
        trigger: null, // Dispara imediatamente
      });
    } catch (error) {
      console.error('Erro ao enviar notificação:', error);
    }
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Exemplo de Notificações Push</Text>
      <Button title="Enviar Notificação" onPress={sendNotification} />
    </View>
  );
}
