import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native';
import WebsiteButton from '../components/WebsiteButton';
import { useTheme } from '../components/ThemeContext';

export default function Home() {
  const apiUrl = "http://192.168.15.2:3000/alert";
  const [data, setData] = useState([]);
  const { isDarkMode } = useTheme();
  const styles = isDarkMode ? darkStyles : lightStyles;

  useEffect(() => {
    fetchData();
  }, [data]);

  const fetchData = async () => {
    try {
      const response = await fetch(apiUrl);
      const result = await response.json();

      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <ScrollView style={[styles.container, { backgroundColor: isDarkMode ? '#242424' : '#eee' }]}>
      <Image
        source={{ uri: "https://cdn.discordapp.com/attachments/1230324585745088564/1230903204938645564/DVGC_MOBILE.png?ex=663502f1&is=66228df1&hm=df4e93d7dda2bf69abb14b1b12e75881a2b79b419aa97b8143a3a80a8aafbc09&" }}
        style={styles.logo}
      />
      <View style={[styles.square, { borderColor: isDarkMode ? '#666' : '#000000' }]}>
        <Text style={[styles.boldText, { color: isDarkMode ? '#eee' : '#000000' }]}>Total de Vazamentos</Text>
        <Text style={[styles.largeText, { color: isDarkMode ? '#eee' : '#000000' }]}>{data.length}</Text>
        <Text></Text>
      </View>
    </ScrollView>
  );
}

const lightStyles = StyleSheet.create({
  container: {
    flex: 1,
  },
  logo: {
    height: 150,
    width: 250,
    alignSelf: "center",
    marginBottom: 20,
    marginTop: 40,
    borderRadius: 15,
    backgroundColor: '#eee',
  },
  square: {
    width: "90%",
    height: 175,
    borderWidth: 1,
    borderRadius: 15,
    alignItems: "center",
    justifyContent: "space-between",
    alignSelf:"center"
  },
  boldText: {
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 20,
    marginBottom: 20,
    marginTop:20
  },
  largeText: {
    fontSize: 50,
    fontWeight: "bold",
    marginBottom: 20,
  },
  passwordContainer: {
    justifyContent: "space-evenly",
    marginTop: 10,
    flexDirection: "row",
    gap: 10,
    borderWidth: 1,
    borderRadius: 15,
    width: "90%",
    alignSelf: "center",
  },
  passwordSection: {
    padding: 20,
    borderRadius: 15,
    gap: 2,
  },
  passwordTitle: {
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 20,
    marginBottom: 20,
  },
  passwordText: {
    textAlign: "center",
    color: '#000000',
  },
});

const darkStyles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#242424',
  },
  logo: {
    height: 80,
    width: 150,
    alignSelf: "center",
    marginBottom: 20,
    marginTop: 40,
  },

  square: {
    width: "90%",
    height: 175,
    borderWidth: 1,
    borderRadius: 15,
    alignItems: "center",
    justifyContent: "space-between",
    borderColor: '#666',
    alignSelf:"center"
  },
  boldText: {
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 20,
    marginBottom: 20,
    color: '#eee',
    marginTop:20
  },
  largeText: {
    fontSize: 50,
    fontWeight: "bold",
    marginBottom: 20,
    color: '#eee',
  },
  passwordContainer: {
    justifyContent: "space-evenly",
    marginTop: 10,
    flexDirection: "row",
    gap: 10,
    borderWidth: 1,
    borderRadius: 15,
    width: "90%",
    alignSelf: "center",
    borderColor: '#666',
  },
  passwordSection: {
    padding: 20,
    borderRadius: 15,
    gap: 2,
  },
  passwordTitle: {
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 20,
    marginBottom: 20,
    color: '#eee',
  },
  passwordText: {
    textAlign: "center",
    color: '#eee',
  },
});
